import React, { useState, useEffect } from "react";

const BASE_URL = "http://localhost:3000";

function Materials({ user, setUser }) {
  const [inventory, setInventory] = useState([]);

  // ✅ Move fetchInventory inside useEffect to fix ESLint warning
  useEffect(() => {
    const fetchInventory = async () => {
      const res = await fetch(`${BASE_URL}/inventory/${user.id}`);
      const data = await res.json();
      setInventory(data);
    };
    fetchInventory();
  }, [user.id]); // only depends on user.id

  const handleAddItem = async () => {
    const itemName = prompt("Enter item name:");
    const qty = parseInt(prompt("Enter quantity:"), 10);
    if (!itemName || isNaN(qty)) return;

    await fetch(`${BASE_URL}/inventory`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id: user.id, item_name: itemName, quantity: qty }),
    });
    // refetch inventory after adding
    const res = await fetch(`${BASE_URL}/inventory/${user.id}`);
    const data = await res.json();
    setInventory(data);
  };

  const handleUpdateItem = async (id, currentQty) => {
    const qty = parseInt(prompt("Enter new quantity:", currentQty), 10);
    if (isNaN(qty)) return;
    await fetch(`${BASE_URL}/inventory/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ quantity: qty }),
    });
    const res = await fetch(`${BASE_URL}/inventory/${user.id}`);
    const data = await res.json();
    setInventory(data);
  };

  const handleDeleteItem = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    await fetch(`${BASE_URL}/inventory/${id}`, { method: "DELETE" });
    const res = await fetch(`${BASE_URL}/inventory/${user.id}`);
    const data = await res.json();
    setInventory(data);
  };

  const handleLogout = () => setUser(null);

  return (
    <div className="container">
      <div className="inventory-section">
        <div className="section-title">
          <h2>{user.username}'s Inventory</h2>
          <div>
            <button className="add" onClick={handleAddItem}>Add Item</button>
            <button className="delete" onClick={handleLogout}>Logout</button>
          </div>
        </div>
        <ul>
          {inventory.map((item) => (
            <li key={item.id}>
              <span>{item.item_name} - {item.quantity}</span>
              <div>
                <button className="update" onClick={() => handleUpdateItem(item.id, item.quantity)}>Update</button>
                <button className="delete" onClick={() => handleDeleteItem(item.id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
