import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { BASE_URL } from "../config";

export default function AreaCalculator({ user, setUser }) {
    const navigate = useNavigate();
    const mapRef = useRef(null);
    const googleMapRef = useRef(null);
    const drawingManagerRef = useRef(null);
    const currentShapeRef = useRef(null);

    const [area, setArea] = useState(0);
    const [unit, setUnit] = useState("sqft");
    const [isLoaded, setIsLoaded] = useState(false);
    const [userLocation, setUserLocation] = useState(null);

    // Save functionality
    const [customers, setCustomers] = useState([]);
    const [jobs, setJobs] = useState([]);
    const [selectedCustomer, setSelectedCustomer] = useState("");
    const [selectedJob, setSelectedJob] = useState("");
    const [areaName, setAreaName] = useState("");
    const [showSaveForm, setShowSaveForm] = useState(false);
    const [saveHover, setSaveHover] = useState(false);

    useEffect(() => {
        // Get user's current location
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    setUserLocation({
                        lat: position.coords.latitude,
                        lng: position.coords.longitude,
                    });
                },
                (error) => {
                    console.log("Geolocation error:", error.message);
                    // Fallback to default location (New York)
                    setUserLocation({ lat: 40.7128, lng: -74.0060 });
                }
            );
        } else {
            // Fallback if geolocation not supported
            setUserLocation({ lat: 40.7128, lng: -74.0060 });
        }

        // Load Google Maps API
        const loadGoogleMaps = () => {
            if (window.google && window.google.maps) {
                setIsLoaded(true);
                return;
            }

            const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

            if (!apiKey) {
                console.error("Google Maps API key not found. Please set REACT_APP_GOOGLE_MAPS_API_KEY in your .env.local file");
                return;
            }

            const script = document.createElement("script");
            script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=drawing,geometry`;
            script.async = true;
            script.defer = true;
            script.onload = () => setIsLoaded(true);
            script.onerror = () => {
                console.error("Failed to load Google Maps API. Please check your API key.");
            };
            document.head.appendChild(script);
        };

        loadGoogleMaps();

        // Fetch customers
        fetchCustomers();
    }, []);

    const fetchCustomers = async () => {
        try {
            const res = await fetch(`${BASE_URL}/customers/${user.id}`);
            if (res.ok) {
                const data = await res.json();
                setCustomers(data);
            }
        } catch (err) {
            console.error("Error fetching customers:", err);
        }
    };

    useEffect(() => {
        if (!isLoaded || !mapRef.current || !userLocation) return;

        // Initialize map with user's location
        const map = new window.google.maps.Map(mapRef.current, {
            center: userLocation,
            zoom: 18, // Closer zoom for user's location
            mapTypeId: "satellite",
            mapTypeControl: true,
            streetViewControl: false,
            fullscreenControl: true,
        });

        googleMapRef.current = map;

        // Add a marker at user's location
        new window.google.maps.Marker({
            position: userLocation,
            map: map,
            title: "Your Location",
            icon: {
                path: window.google.maps.SymbolPath.CIRCLE,
                scale: 8,
                fillColor: "#4DA3A2",
                fillOpacity: 1,
                strokeColor: "#ffffff",
                strokeWeight: 2,
            },
        });

        // Initialize drawing manager
        const drawingManager = new window.google.maps.drawing.DrawingManager({
            drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
            drawingControl: true,
            drawingControlOptions: {
                position: window.google.maps.ControlPosition.TOP_CENTER,
                drawingModes: [
                    window.google.maps.drawing.OverlayType.POLYGON,
                    window.google.maps.drawing.OverlayType.RECTANGLE,
                    window.google.maps.drawing.OverlayType.CIRCLE,
                ],
            },
            polygonOptions: {
                fillColor: "#4DA3A2",
                fillOpacity: 0.3,
                strokeWeight: 2,
                strokeColor: "#234848",
                clickable: true,
                editable: true,
                zIndex: 1,
            },
            rectangleOptions: {
                fillColor: "#4DA3A2",
                fillOpacity: 0.3,
                strokeWeight: 2,
                strokeColor: "#234848",
                clickable: true,
                editable: true,
                zIndex: 1,
            },
            circleOptions: {
                fillColor: "#4DA3A2",
                fillOpacity: 0.3,
                strokeWeight: 2,
                strokeColor: "#234848",
                clickable: true,
                editable: true,
                zIndex: 1,
            },
        });

        drawingManager.setMap(map);
        drawingManagerRef.current = drawingManager;

        // Listen for shape completion
        window.google.maps.event.addListener(
            drawingManager,
            "overlaycomplete",
            (event) => {
                // Remove previous shape
                if (currentShapeRef.current) {
                    currentShapeRef.current.setMap(null);
                }

                currentShapeRef.current = event.overlay;
                calculateArea(event.overlay, event.type);

                // Add listeners for editing
                if (event.type === "polygon") {
                    window.google.maps.event.addListener(
                        event.overlay.getPath(),
                        "set_at",
                        () => calculateArea(event.overlay, event.type)
                    );
                    window.google.maps.event.addListener(
                        event.overlay.getPath(),
                        "insert_at",
                        () => calculateArea(event.overlay, event.type)
                    );
                } else if (event.type === "rectangle") {
                    window.google.maps.event.addListener(
                        event.overlay,
                        "bounds_changed",
                        () => calculateArea(event.overlay, event.type)
                    );
                } else if (event.type === "circle") {
                    window.google.maps.event.addListener(
                        event.overlay,
                        "radius_changed",
                        () => calculateArea(event.overlay, event.type)
                    );
                    window.google.maps.event.addListener(
                        event.overlay,
                        "center_changed",
                        () => calculateArea(event.overlay, event.type)
                    );
                }

                // Switch to hand mode after drawing
                drawingManager.setDrawingMode(null);
            }
        );
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [isLoaded, userLocation]);

    const calculateArea = (shape, type) => {
        let areaInSquareMeters = 0;

        if (type === "polygon") {
            areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(
                shape.getPath()
            );
        } else if (type === "rectangle") {
            const bounds = shape.getBounds();
            const ne = bounds.getNorthEast();
            const sw = bounds.getSouthWest();
            const nw = new window.google.maps.LatLng(ne.lat(), sw.lng());
            const se = new window.google.maps.LatLng(sw.lat(), ne.lng());

            const path = [nw, ne, se, sw];
            areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(path);
        } else if (type === "circle") {
            const radius = shape.getRadius();
            areaInSquareMeters = Math.PI * radius * radius;
        }

        setArea(areaInSquareMeters);
    };

    const convertArea = (areaInSquareMeters) => {
        switch (unit) {
            case "sqft":
                return (areaInSquareMeters * 10.7639).toFixed(2);
            case "sqm":
                return areaInSquareMeters.toFixed(2);
            case "acres":
                return (areaInSquareMeters * 0.000247105).toFixed(4);
            case "hectares":
                return (areaInSquareMeters * 0.0001).toFixed(4);
            default:
                return areaInSquareMeters.toFixed(2);
        }
    };

    const clearShape = () => {
        if (currentShapeRef.current) {
            currentShapeRef.current.setMap(null);
            currentShapeRef.current = null;
            setArea(0);
        }
    };

    const getUnitLabel = () => {
        switch (unit) {
            case "sqft":
                return "sq ft";
            case "sqm":
                return "sq m";
            case "acres":
