import React, { useState, useEffect } from "react";
import { getUsers, addUser, getInventory, addInventory } from "./api/api";
import "./styles.css";

function App() {
  const [users, setUsers] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [newUser, setNewUser] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const data = await getUsers();
    setUsers(data);
  };

  const handleAddUser = async () => {
    if (!newUser) return;
    await addUser(newUser, "password123");
    setNewUser("");
    fetchUsers();
  };

  const handleSelectUser = async (userId) => {
    setSelectedUser(userId);
    const inv = await getInventory(userId);
    setInventory(inv);
  };

  const handleAddItem = async () => {
    const itemName = prompt("Enter item name:");
    const qty = parseInt(prompt("Enter quantity:"), 10);
    if (!itemName || isNaN(qty)) return;
    await addInventory(selectedUser, itemName, qty);
    const inv = await getInventory(selectedUser);
    setInventory(inv);
  };

  const handleUpdateItem = async (id, currentQty) => {
    const qty = parseInt(prompt("Enter new quantity:", currentQty), 10);
    if (isNaN(qty)) return;
    await fetch(`http://localhost:3000/inventory/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ quantity: qty }),
    });
    const inv = await getInventory(selectedUser);
    setInventory(inv);
  };

  const handleDeleteItem = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    await fetch(`http://localhost:3000/inventory/${id}`, { method: "DELETE" });
    const inv = await getInventory(selectedUser);
    setInventory(inv);
  };

  return (
    <div className="container">
      {/* Users Section */}
      <div className="users-section">
        <div className="section-title">
          <h2>Users</h2>
        </div>
        <div>
          <input
            type="text"
            value={newUser}
            placeholder="New username"
            onChange={(e) => setNewUser(e.target.value)}
          />
          <button className="add" onClick={handleAddUser}>Add User</button>
        </div>
        <ul>
          {users.map((user) => (
            <li key={user.id}>
              <span>{user.username}</span>
              <button className="update" onClick={() => handleSelectUser(user.id)}>View Inventory</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Inventory Section */}
      {selectedUser && (
        <div className="inventory-section">
          <div className="section-title">
            <h2>Inventory</h2>
            <button className="add" onClick={handleAddItem}>Add Item</button>
          </div>
          <ul>
            {inventory.map((item) => (
              <li key={item.id}>
                <span>{item.item_name} - {item.quantity}</span>
                <div>
                  <button className="update" onClick={() => handleUpdateItem(item.id, item.quantity)}>Update</button>
                  <button className="delete" onClick={() => handleDeleteItem(item.id)}>Delete</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
